import React, { useState } from 'react';

import Button from '../UI/Button/Button';
import NewService from './Service/NewService';
import classes from './CarServices.module.css';

const CarServices = (props) => {
  const [showAddForm, setShowAddForm] = useState();

  const carServices = props.allCarServices.map((service, pos) => {
    return (
      <div key={pos} className={classes['car-services-list']}>
        <ul>
          <li>
            service: <p>{service.service.join(', ')}</p>
          </li>
          <li>
            date: <p>{service.date}</p>
          </li>
          <li>
            mileage: <p>{service.mileage} km</p>
          </li>
          <li>
            info: <p>{service.info}</p>
          </li>
        </ul>
      </div>
    );
  });

  const onAddNewService = () => {
    setShowAddForm(true);
    props.addNewService('asd');
  };

  return (
    <div className={classes['car-services']}>
      <div className={classes['car-services-box']}>{carServices}</div>
      {showAddForm && <NewService />}
      {!showAddForm && <Button className={classes['button-add']} onClick={onAddNewService}>
        Add New Service
      </Button>}
    </div>
  );
};

export default CarServices;
