import React, {useContext} from 'react';

import Wrapper from '../../UI/Wrapper/Wrapper';
import CarServices from '../Book/CarServices';
import CarsContext from '../../../store/cars-context';

import classes from './CarInfo.module.css';

const CarInfo = (props) => {



  const pickedCar = props.cars.filter((car) => car.carId === props.loadCarId);

  const onAddHandler = (service) => {
    const getServices = pickedCar.map((car) => {
      return car.services;
    });
    getServices[0].push(service);
    props.editCar(pickedCar);
  };

  const infoCar = pickedCar.map((car) => {
    return (
        <div key={car.id} className={classes['car-info-list']}>
          <ul>
            <li>
              brand:<p>{car.brand}</p>
            </li>
            <li>
              series:<p>{car.series}</p>
            </li>
            <li>
              model:<p>{car.model}</p>
            </li>
            <li>
              year:<p>{car.year}</p>
            </li>
            <li>
              engine:<p>{car.engine}</p>
            </li>
            <li>
              mileage:<p>{car.mileage}</p>
            </li>
          </ul>
        </div>
    );
  });

  
  const services = [];
  const carService = pickedCar.reduce((service, car) => car.services, {});
  for (const service in carService) {
    services.push({
      id: service,
      service: carService[service].service,
      mileage: carService[service].mileage,
      date: carService[service].date,
      info: carService[service].info,
    });
  }

  return (
    <Wrapper>
      {infoCar}
      <CarServices services={services} sendNewService={onAddHandler} />
    </Wrapper>
  );
};

export default CarInfo;
