import React, {useContext} from 'react';
import CarsContext from '../../../store/cars-context';

import classes from './CarsList.module.css';

const CarsList = (props) => {
  const carsCtx = useContext(CarsContext);

  const pickCar = (event) => {
    const carId = event.target.dataset.id;
    props.pickedCar(carId);
  };
  const listCars = carsCtx.cars.map((car) => {
    return (
      <li key={car.id}>
        <button onClick={pickCar} data-id={car.id}>
          {car.brand} {car.series}
        </button>
      </li>
    );
  });

  return (
    <div className={classes['cars-list']}>
      <ul>{listCars}</ul>
    </div>
  );
};

export default CarsList;
