import React, { useState, useContext } from 'react';

import Wrapper from '../UI/Wrapper/Wrapper';
import CarInfo from './Info/CarInfo';

import classes from './Cars.module.css';

const Cars = (props) => {

  const [loadCarInfo, setLoadCarInfo] = useState(props.cars[0].id);

  const onPickedCar = (event) => {
    setLoadCarInfo(event.target.dataset.id);
  };

  const listCars = props.cars.map((car) => {
    return (
      <li key={car.id}>
        <button onClick={onPickedCar} data-id={car.id}>
          {car.brand} {car.series}
        </button>
      </li>
    );
  });

  const onEditHandler = (car) => {
    
  };

  return (
    <Wrapper>
      <div className={classes['cars-list']}>
        <ul>{listCars}</ul>
      </div>
      <CarInfo loadCarId={loadCarInfo} />
    </Wrapper>
  );
};

export default Cars;
