  }

  const onClickAdd = () => {
    setShowAddForm(true);
  };

  return (
    <div className={classes['car-services']}>
      <div className={classes['car-services-box']}>{carServices}</div>
      {showAddForm && <NewService carNewService={addNewService}/>}
      {!showAddForm && <Button className={classes['button-add']} onClick={onClickAdd}>
        Add New Service
      </Button>}
    </div>
  );
};

export default CarServices;
