import React, { useState, useEffect } from 'react';

import classes from './CarsList.module.css';

const CarsList = (props) => {

  
  const [activeTab, setActiveTab] = useState();

  const pickCar = (event) => {
    const carId = event.target.dataset.id;
    props.pickedCar(carId);

  
    setActiveTab(true);
    useEffect(() => {
      if(isActive) {
        event.target.classList.add('active');
      } else {
        event.target.classList.remove('active');
      }
    }, [isActive]);
    //event.target.className = activeTab ? 'active' : null;
  };

  

  

  const listCars = props.carsList.map((cars) => {
    return cars.map((car) => {
      return (
        <li key={car.id} >
          <button
            onClick={pickCar}
            data-id={car.id}
            
          >
            {car.brand} {car.series}
          </button>
        </li>
      );
    });
  });

  return (
    <div className={classes['cars-list']}>
      <ul>{listCars}</ul>
    </div>
  );
};

export default CarsList;
