import React, { useState, useEffect, useRef } from 'react';

import classes from './CarsList.module.css';

const CarsList = (props) => {

  const refLi = useRef(null);
  let [isActive, setActiveTab] = useState();

  const pickCar = (event) => {
    const carId = event.target.dataset.id;
    props.pickedCar(carId);

  
    setActiveTab(event.target.dataset.id);
    isActive = false;
    //event.target.className = activeTab ? 'active' : null;
  };
  console.log(isActive);

  // useEffect(() => {
  //   if(isActive) {
  //     console.log(refLi.current + 'true');
  //   } else {
  //     console.log(refLi.current + 'else');
  //   }
  // }, [isActive]);

  

  const listCars = props.carsList.map((cars) => {
    return cars.map((car) => {
      return (
        <li key={car.id} >
          <button
            onClick={pickCar}
            data-id={car.id}
          >
            {car.brand} {car.series}
          </button>
        </li>
      );
    });
  });

  return (
    <div className={classes['cars-list']}>
      <ul>{listCars}</ul>
    </div>
  );
};

export default CarsList;
