import React from 'react';
import Wrapper from '../UI/Wrapper/Wrapper';

import classes from './CarInfo.module.css';
import CarServices from './CarServices';

const CarInfo = (props) => {
  const getCar = props.allCars.filter((car) => car.id === props.loadCarId);

  const onAddHandler = (service) => {
    const getServices = getCar.map(car => {
      return car.services;
    });
    getServices[0].push(service);
    props.editCar(getCar);
  };

  const infoCar = getCar.map((car) => {
    return (
      <Wrapper key={car.id}>
        <div className={classes['car-info-list']}>
          <ul>
            <li>
              brand:<p>{car.brand}</p>
            </li>
            <li>
              series:<p>{car.series}</p>
            </li>
            <li>
              model:<p>{car.model}</p>
            </li>
            <li>
              year:<p>{car.year}</p>
            </li>
            <li>
              engine:<p>{car.engine}</p>
            </li>
            <li>
              mileage:<p>{car.mileage}</p>
            </li>
          </ul>
        </div>
        <CarServices
          allCarServices={car.services}
          sendNewService={onAddHandler}
        />
      </Wrapper>
    );
  });
  

  return <div className={classes['car-info']}>{infoCar}</div>;
};

export default CarInfo;
