import React, { useState, useEffect } from 'react';

import Cars from '../Cars/Cars';
import Img from '../UI/Img/Img';
import Card from '../UI/Card/Card';
import Wrapper from '../UI/Wrapper/Wrapper';

import classes from './Garage.module.css';
import garageImage from '../../assets/Awesome-garage.jfif';

const Garage = (props) => {
  const [user, setUser] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(false);

  useEffect(() => {
    const fetchCars = async () => {
      setIsLoading(true);
      const response = await fetch(
        'https://react-http2-d8099-default-rtdb.firebaseio.com/users.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedUser = [];

      for (const key in responseData) {
        loadedUser.push({
          id: key,
          email: responseData[key].email,
          cars: responseData[key].cars,
        });
      }

      setUser(loadedUser);
      setIsLoading(false);
    };

    fetchCars().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);

  if(isLoading) {
    return (
      <Card><p>Loading...</p></Card>
    );
  }

  if(httpError) {
    return (
      <Card><p>{httpError}</p></Card>
    )
  }

  const userCars = user.cars;
  const cars = [];
  for(const key in userCars) {
    cars.push({
      id: key,
      brand: userCars[key].brand,
      model: userCars[key].model
    })
  }

  console.log(cars);


  return (
    <Wrapper>
      <Img src={garageImage} alt="Awesome garage" />
      <Card className={classes.garage}>
        <Cars cars={cars}/>
      </Card>
    </Wrapper>
  );
};

export default Garage;
