import React, { useState, useEffect, useContext } from 'react';

import Cars from '../Cars/Cars';
import Card from '../UI/Card/Card';

import Img from '../UI/Img/Img';
import classes from './Garage.module.css';
import garageImage from '../../assets/Awesome-garage.jfif';
import CarsContext from '../../store/cars-context';
import Wrapper from '../UI/Wrapper/Wrapper';

import { db } from '../../firebase';
import { onValue, ref } from 'firebase/database';

const Garage = (props) => {
  const carsCtx = useContext(CarsContext);

  const [userData, setUserData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(false);

  useEffect(() => {
    onValue(ref(db), (snapshot) => {
      const data = snapshot.val();
      if (data !== null) {
        Object.values(data).map((user) => {
          setUserData((oldArray) => [...oldArray, user]);
        });
      }
      console.log(data);
    });
  }, []);

  if (isLoading) {
    return (
      <Card>
        <p>Loading...</p>
      </Card>
    );
  }

  if (httpError) {
    return (
      <Card>
        <p>{httpError}</p>
      </Card>
    );
  }

  const user = Object.assign({}, userData);
  console.log(user);
  const loadedUser = [];

  for (const key in user) {
    loadedUser.push({
      id: key,
      email: user[key].email,
    });
  }

  const loadedCars = [];

  for (const value of Object.values(user)) {
    const cars = value.cars;
    for (const car in cars) {
      loadedCars.push({
        id: car,
        brand: cars[car].brand,
        series: cars[car].series,
        services: cars[car].services,
      });
    }
  }

  carsCtx.cars = loadedCars;

  return (
    <Wrapper>
      <Img src={garageImage} alt="Awesome garage" />
      <Card className={classes.garage}>
        <Cars />
      </Card>
    </Wrapper>
  );
};

export default Garage;
