import React, { useState, useContext } from 'react';

import Wrapper from '../UI/Wrapper/Wrapper';
import CarsContext from '../../store/cars-context';
import CarInfo from './Info/CarInfo';

import classes from './Cars.module.css';
import AddCar from './Info/AddCar';

import { db } from '../../firebase';

import { set, ref } from 'firebase/database';

const Cars = (props) => {
  const carsCtx = useContext(CarsContext);
  const [addCarForm, setAddCarForm] = useState(false);
  // const [loadCarInfo, setLoadCarInfo] = useState(cars[0].id);

  
  const loadedCars = [];
  
  console.log(props.cars);
const cars = props.cars;
    for (const car in cars) {
      loadedCars.push({
        id: car,
        brand: cars[car].brand,
        series: cars[car].series,
        services: cars[car].services,
      });
    }
  


  // const onPickedCar = (event) => {
  //   setLoadCarInfo(event.target.dataset.id);
  //   setAddCarForm(false);
  // };

  // const listCars = cars.map((car) => {
  //   return (
  //     <li key={car.id}>
  //       <button onClick={onPickedCar} data-id={car.id}>
  //         {car.brand} {car.series}
  //       </button>
  //     </li>
  //   );
  // });

  const onClickAddCar = () => {
    setAddCarForm(true);
  };

  const onAddCarHandler = (car) => {
    const carId = car.carId;
    set(ref(db, `/${props.userId}/cars/${carId}`), {
      brand: car.brand,
      model: car.model,
      series: car.series,
      year: car.year,
      milear: car.mileage,
      serivces: 'none'
    });
  };

  const onCloseHandler = () => {
    // setLoadCarInfo(cars[cars.length - 1].id);
    setAddCarForm(false);
  };

  return (
    <Wrapper>
      <div className={classes['cars-list']}>
        <ul>
          {/* {listCars} */}
          <li>
            <button onClick={onClickAddCar}>Add Car</button>
          </li>
        </ul>
      </div>
      <div className={classes['car-info']}>
        {/* {!addCarForm && <CarInfo loadCarId={loadCarInfo} />} */}
        {addCarForm && (
          <AddCar onAdd={onAddCarHandler} onClose={onCloseHandler} />
        )}
      </div>
    </Wrapper>
  );
};

export default Cars;
