import React, { useState, useEffect } from 'react';

import Cars from '../Cars/Cars';
import Img from '../UI/Img/Img';
import Card from '../UI/Card/Card';
import Wrapper from '../UI/Wrapper/Wrapper';

import classes from './Garage.module.css';
import garageImage from '../../assets/Awesome-garage.jfif';

const Garage = (props) => {

  const [cars, setCars] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(false);

  useEffect(()=> {
    const fetchCars = async () => {
      setIsLoading(true);
      const response = await fetch('https://car-mood.firebaseio.com/car-mood.json');

      if(!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      console.log(responseData);


      const loadedCars = [];

      for (const key in responseData) {
        loadedCars.push({
          id: key,
          email: responseData[key].email,
          cars: responseData[key].cars
        })
      }
    }

    fetchCars().catch((error)=> {
      setIsLoading(false);
      setHttpError(error.message);
    })
  }, [])

  return (
    <Wrapper>
      <Img src={garageImage} alt="Awesome garage" />
      <Card className={classes.garage}>
        {/* <Cars userCars={cars[0]} editCars={onEditCars} /> */}
      </Card>
    </Wrapper>
  );
};

export default Garage;
