import React, { useState, useContext } from 'react';

import Wrapper from '../UI/Wrapper/Wrapper';
import CarsContext from '../../../store/cars-context';
import CarInfo from './Info/CarInfo';

const Cars = (props) => {
  const carsCtx = useContext(CarsContext);
  const cars = carsCtx.cars;
  const [loadCarInfo, setLoadCarInfo] = useState('car1');

  const onPickedCar = (carId) => {
    setLoadCarInfo(carId);
  };

  const listCars = cars.map((car) => {
    return (
      <li key={car.id}>
        <button onClick={onPickedCar} data-id={car.id}>
          {car.brand} {car.series}
        </button>
      </li>
    );
  });

  const onEditHandler = (car) => {
    props.editCars(car);
  }

  return (
    <Wrapper>
      <div className={classes['cars-list']}>
      <ul>{listCars}</ul>
    </div>
      <CarInfo loadCarId={loadCarInfo} editCar={onEditHandler}/>
    </Wrapper>
  );
};

export default Cars;
