import React, { useState, useRef, useEffect } from 'react';

import Input from '../../UI/Input/Input';
import Button from '../../UI/Button/Button';

import classes from './NewService.module.css';

const NewService = (props) => {
  const [serviceName, setServiceName] = useState('');
  const [nameIsValid, setNameIsValid] = useState();
  const [serviceDate, setServiceDate] = useState('');
  const [dateIsValid, setDateIsValid] = useState();
  const [serviceMileage, setServiceMileage] = useState('');
  const [mileageIsValid, setMileageIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const infoService = useRef(null);

  useEffect(() => {
    setFormIsValid(serviceName && serviceDate && serviceMileage);
  }, [serviceName, serviceDate, serviceMileage]);

  const serviceChangeHandler = (event) => {
    setServiceName(event.target.value);
  };

  const validateServiceHandler = () => {
    setNameIsValid(serviceName !== '');
  };

  const dateChangeHandler = (event) => {
    setServiceDate(new Date(event.target.value));
  };

  const validateDateHandler = () => {
    setDateIsValid(serviceDate !== '');
  };

  const mileageChangeHandler = (event) => {
    setServiceMileage(event.target.value);
  };

  const validateMileageHandler = () => {
    setMileageIsValid(serviceMileage !== '');
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const info = infoService.current.value;
    const date = serviceDate;
    // const newService = {
    //     service: serviceName,
    //     date,
    //     mileage: serviceMileage,
    //     info
    // }
    console.log(date);
  };

  return (
    <div className={classes['new-service']}>
      <div className={classes['service-form']}>
        <form>
          <Input
            id="service"
            label="Service"
            type="text"
            isValid={nameIsValid}
            value={serviceName}
            onChange={serviceChangeHandler}
            onBlur={validateServiceHandler}
            className={`${classes.control} ${classes['service-name']}`}
          />
          <div className={classes['service-date-box']}>
            <Input
              id="date"
              label="Date"
              type="date"
              isValid={dateIsValid}
              value={serviceDate}
              onChange={dateChangeHandler}
              onBlur={validateDateHandler}
              className={`${classes.control} ${classes['service-date']}`}
            />
            <Input
              id="mileage"
              label="Mileage"
              type="number"
              isValid={mileageIsValid}
              value={serviceMileage}
              onChange={mileageChangeHandler}
              onBlur={validateMileageHandler}
              className={`${classes.control} ${classes['service-mileage']}`}
            />
          </div>
          <div className={classes.control}>
            <label htmlFor="info">Info</label>
            <input id="info" label="Info" type="text" ref={infoService} />
          </div>
        </form>
        <div className={classes.actions}>
          <Button type="submit" onClick={submitHandler} className={classes.btn} disabled={!formIsValid}>
            Add Service
          </Button>
        </div>
      </div>
    </div>
  );
};

export default NewService;
