import React, { useState } from 'react';

import Wrapper from '../UI/Wrapper/Wrapper';
import CarsList from './CarsList';
import CarInfo from './CarInfo';

const Cars = (props) => {
  const [loadCarInfo, setLoadCarInfo] = useState('1');

  // const listCars = props.userCars.map((cars) => {
  //   return cars.map((car) => {
  //     return cars = {
  //       id: car.id,
  //       brand: car.brand,
  //       series: car.series,
  //     };
  //   });
  // });

  const infoForCar = props.userCars.map((cars) => {
    return cars.map((car) => {
      if (car.id !== props.loadCarId) {
        return null;
      }
      return (
        <ul key={car.id}>
          <li>
            brand:<p>{car.brand}</p>
          </li>
          <li>
            series:<p>{car.series}</p>
          </li>
          <li>
            model:<p>{car.model}</p>
          </li>
          <li>
            year:<p>{car.year}</p>
          </li>
          <li>
            engine:<p>{car.engine}</p>
          </li>
          <li>
            mileage:<p>{car.mileage}</p>
          </li>
        </ul>
      );
    });
  });

  const onPickedCar = (carId) => {
    setLoadCarInfo(carId);
  };

  return (
    <Wrapper>
      <CarsList carsList={props.userCars} pickedCar={onPickedCar} />
      <CarInfo allCars={props.userCars} loadCarId={loadCarInfo} />
    </Wrapper>
  );
};

export default Cars;
